mapper.select:
SELECT task_id, task_name, creator_id, executor_id, start_location, distance, deadline_time, completion_time, upload_time, status, create_time FROM task
AND task_name LIKE CONCAT('%', #{taskName}, '%')
AND status = #{status}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
ORDER BY create_time DESC

mapper.insert:
INSERT INTO task (
    task_name, creator_id, executor_id, start_location, 
    distance, deadline_time, completion_time, upload_time, 
    status, create_time
)
VALUES (
    #{taskName}, #{creatorId}, #{executorId}, #{startLocation},
    #{distance}, #{deadlineTime}, #{completionTime}, #{uploadTime},
    #{status}, #{createTime}
)
