mapper.update:
UPDATE user
SET password = "123456"
WHERE user_id = #{userId}

UPDATE user_role
SET role_id = #{roleId}
WHERE user_id = #{userId}

mapper.select:
SELECT u.user_id, u.username, u.nickname, u.dept_id, d.dept_name, u.phone_number, u.status, u.create_time, u.remark, GROUP_CONCAT(r.role_name) AS role_names FROM user u LEFT JOIN department d ON u.dept_id = d.dept_id LEFT JOIN user_role ur ON u.user_id = ur.user_id LEFT JOIN role r ON ur.role_id = r.role_id
AND u.user_id = #{userId}
AND u.username LIKE CONCAT('%', #{username}, '%')
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND u.dept_id = #{deptId}
AND u.phone_number LIKE CONCAT('%', #{phoneNumber}, '%')
AND u.status = #{status}
AND u.create_time >= #{startTime}
AND u.create_time <= #{endTime}
GROUP BY u.user_id ORDER BY u.user_id ASC LIMIT #{offset}, #{pageSize}

SELECT COUNT(DISTINCT u.user_id)
FROM user u
LEFT JOIN department d ON u.dept_id = d.dept_id
LEFT JOIN user_role ur ON u.user_id = ur.user_id
LEFT JOIN role r ON ur.role_id = r.role_id


        AND u.user_id = #{userId}


        AND u.username LIKE CONCAT('%', #{username}, '%')


        AND u.nickname LIKE CONCAT('%', #{nickname}, '%')


        AND u.dept_id = #{deptId}


        AND u.phone_number LIKE CONCAT('%', #{phoneNumber}, '%')


        AND u.status = #{status}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}
